from datetime import datetime

from django.db import models
from django.contrib.auth.models import AbstractUser
# Create your models here.


class User(AbstractUser):
    portrait = models.TextField(verbose_name="用户头像", default="data:image/png;base64,")
    gender = models.CharField(max_length=5, verbose_name="性别", default="男")
    college = models.IntegerField(verbose_name="所在学院", default=0)
    birth_year = models.IntegerField(default=1990, verbose_name="出生年份")
    birth_month = models.IntegerField(default=1, verbose_name="出生月份")
    emotion = models.CharField(max_length=10, verbose_name="情感状态", default="单身")
    sign = models.TextField(verbose_name="个性签名", default="")
    reply_authority = models.IntegerField(default=3, verbose_name="我的回复访问权限")
    publish_authority = models.IntegerField(default=3, verbose_name="我的发表访问权限")
    follow_authority = models.IntegerField(default=3, verbose_name="我的关注访问权限")
    fans_authority = models.IntegerField(default=3, verbose_name="我的粉丝访问权限")
    un_read_reply_num = models.IntegerField(default=0, verbose_name="未读回复数")
    un_read_fans_num = models.IntegerField(default=0, verbose_name="未读粉丝数")

    class Meta:
        verbose_name = "用户信息"
        verbose_name_plural = verbose_name

    def __str__(self):
        return self.username


class Post(models.Model):
    title = models.CharField(max_length=200, verbose_name="标题", default="")
    type = models.CharField(max_length=20, verbose_name="分区", default="live")
    author = models.ForeignKey(User, on_delete=models.CASCADE, verbose_name="作者", null=True, blank=True)
    publish_time = models.DateTimeField(default=datetime.now, verbose_name="发布时间")
    click_num = models.IntegerField(default=0, verbose_name="浏览数")
    reply_num = models.IntegerField(default=0, verbose_name="回复数")
    is_boutique = models.BooleanField(default=False, verbose_name="是否为精品")
    content = models.TextField(default="", verbose_name="内容")

    class Meta:
        verbose_name = "帖子信息"
        verbose_name_plural = verbose_name

    def __str__(self):
        return self.title


class Reply(models.Model):
    post = models.ForeignKey(Post, on_delete=models.CASCADE, verbose_name="帖子", null=True, blank=True)
    author = models.ForeignKey(User, on_delete=models.CASCADE, verbose_name="作者", null=True, blank=True)
    publish_time = models.DateTimeField(default=datetime.now, verbose_name="发布时间")
    content = models.TextField(default="", verbose_name="内容")

    class Meta:
        verbose_name = "回复信息"
        verbose_name_plural = verbose_name

    def __str__(self):
        return self.content


class Banner(models.Model):
    banner_img = models.TextField(verbose_name="轮播图资源")
    title = models.CharField(max_length=200, verbose_name="标题", default="")
    content = models.TextField(verbose_name="内容", default="")
    href = models.TextField(verbose_name="链接", default="/index/")
    add_time = models.DateTimeField(default=datetime.now, verbose_name="添加时间")

    class Meta:
        verbose_name = "轮播图信息"
        verbose_name_plural = verbose_name

    def __str__(self):
        return self.title


class Topic(models.Model):
    name = models.CharField(max_length=30, verbose_name="话题名称", default="")
    follow_num = models.IntegerField(default=0, verbose_name="话题关注人数")
    add_time = models.DateTimeField(default=datetime.now, verbose_name="添加时间")

    class Meta:
        verbose_name = "话题信息"
        verbose_name_plural = verbose_name

    def __str__(self):
        return self.name


class TopicPost(models.Model):
    topic = models.ForeignKey(Topic, on_delete=models.CASCADE, verbose_name="所属话题", null=True, blank=True)
    title = models.CharField(max_length=200, verbose_name="标题", default="")
    author = models.ForeignKey(User, on_delete=models.CASCADE, verbose_name="作者", null=True, blank=True)
    publish_time = models.DateTimeField(default=datetime.now, verbose_name="发布时间")
    click_num = models.IntegerField(default=0, verbose_name="浏览数")
    reply_num = models.IntegerField(default=0, verbose_name="回复数")
    is_boutique = models.BooleanField(default=False, verbose_name="是否为精品")
    content = models.TextField(default="", verbose_name="内容")

    class Meta:
        verbose_name = "帖子信息"
        verbose_name_plural = verbose_name

    def __str__(self):
        return self.title


class TopicReply(models.Model):
    post = models.ForeignKey(TopicPost, on_delete=models.CASCADE, verbose_name="帖子", null=True, blank=True)
    author = models.ForeignKey(User, on_delete=models.CASCADE, verbose_name="作者", null=True, blank=True)
    publish_time = models.DateTimeField(default=datetime.now, verbose_name="发布时间")
    content = models.TextField(default="", verbose_name="内容")

    class Meta:
        verbose_name = "回复信息"
        verbose_name_plural = verbose_name

    def __str__(self):
        return self.content


class TopicFollow(models.Model):
    user = models.ForeignKey(User, on_delete=models.CASCADE, verbose_name="用户", null=True, blank=True)
    topic = models.ForeignKey(Topic, on_delete=models.CASCADE, verbose_name="话题", null=True, blank=True)
    follow_time = models.DateTimeField(default=datetime.now, verbose_name="关注时间")

    class Meta:
        verbose_name = "用户关注话题表"
        verbose_name_plural = verbose_name

    def __str__(self):
        return self.user + "->" + self.topic


